import React,{ useState,useRef, useEffect } from 'react';
import BodyText from './boby-text';
import {motion} from 'framer-motion';


const Body=()=>{

    const [createtext,setCreatetext]=useState(0)
    const [textlist,setTextlist]=useState([])
    const [canvassize,setCanvassize]=useState(3);
    const [pararotate,setPararotate]=useState(-135);
    const [paraState,setParaState]=useState(true);
    let canvaswidth=100-(canvassize*1.3);
    let canvasheight=100-(canvassize*1.3);
    let canvasleft=50+(canvassize*1.3)/2;
    let canvastop=50+(canvassize*1.3)/2;
    const elm = useRef(null);
    const bodyRef = useRef();

    const countUp=()=>{
        const updatedText = createtext + 1;
        setCreatetext(updatedText)
        setTextlist(prevList=>[...prevList,''])
    }

    const countDown=()=>{
        if(createtext>=1){
            setCreatetext(prevState => prevState-1)
            setTextlist(prevList=>prevList.slice(0,-1))
        }
    }

    const textValue=(event,index)=>{
        const updatedList = [...textlist];
        updatedList[index] = event.target.value;
        setTextlist(updatedList);
    }

    const sizeValue=(size)=>{
        setCanvassize(size)
    }

    useEffect(()=>{
        if(paraState){
            setPararotate(-225);

        }else{
            setPararotate(-135)

        }


    },[paraState])

    const toggleParaState = (index) => {
        const updatedTextList = textlist.map((text, i) => {
          if (i === index) {
            return !paraState[index];
          } else {
            return paraState[i];
          }
        });
        setParaState(prevstate=>updatedTextList);
      };

    console.log(paraState)
    return(

        <main className="App-body" >

            <div className='textrey'>
                <div className='textparent'>  
                    <button className='genebtn' onClick={countUp}>文字生成</button>
                    <button className='genebtn' onClick={countDown}>削除</button>
                </div>

                <div className='body-item'>
                    {textlist.map((text,index)=>{
                        return(
                            <>
                            <div className='scroll'>
                                <div className='text-item' key={index}>

                                            <input className='textlist'
                                                onChange={(event)=>textValue(event,index)}
                                                type='text'
                                                value={text}
                                            />
                                                    <button className='parastate' onClick={()=>toggleParaState(index)}>スタイル変更&nbsp;
                                                        <motion.div className='allow' animate={{rotate:paraState[index] ? -225 : -135/*pararotate*/}}></motion.div>
                                                    </button>

                                 </div>

                                <BodyText 
                                    text={text}
                                    bodyRef={bodyRef}
                                    textlist={textlist.length}
                                    canvas={sizeValue}
                                    elm={elm}
                                    paraState={paraState[index]}
                                    //calConst={calConst()}


                                />
                               </div>
                        </>
                        )
                    })}

                </div>

                <div  ref={bodyRef} className='canvas'><p style={{position:'absolute',top:'-40px'}}>canvas</p>
                <div className='body-layer'

                        style={{
                            width:`${canvaswidth}%`,
                            height:`${canvasheight}%`,
                            left:`${canvasleft}%`,
                            top:`${canvastop}%`
                            }} 
                        ></div>
                </div>


            </div>

        </main>
    );
}
export default Body;
