import React,{ useState,useEffect } from 'react';
import {motion} from 'framer-motion';

const BodyText=(props)=>{
    const [rotation,setRotation]=useState(0);
    const rotateChange=(event)=>{
        setRotation(event.target.value)
    }
    console.log(rotation);
    useEffect(() => {
        const parsedRotation = parseFloat(rotation);
        if (!isNaN(parsedRotation)) {
          const roundedRotation = Math.round(parsedRotation);
          setRotation(roundedRotation);
        }
      }, [rotation]);

    return(
        <>
        <input  onChange={(event)=>rotateChange(event)}
        type='number'
        value={rotation}

        />

        <motion.div  animate={{rotate:rotation}}>{props.text}</motion.div>


    </>
    );
}
export default BodyText;
