import React, { useState } from "react";

const About=(props)=>{
    const [about,setAbout]=useState(false);
    let aboutpage;

    const AboutClick=()=>{
            setAbout(prevState=>!prevState)      
         }
    aboutpage=about ?(
        <div className='aboutpage'>
            <h1>このページの詳細です</h1>
            <button onClick={AboutClick}>閉じる</button>

        </div>) :(<div></div>)

    return(
        <div>
           {/*  <button  onClick={()=>{AboutClick()}}>
               about
            </button>*/}
            <h2>・ABOUT</h2>
            <p>このwebサイトでは文字生成で好きな文字を生成し、文字を自由に動かし、文字のスタイルを変更
                することで遊んでもらうサイトです。
            </p>
            <p>遊び方</p>
            <p>注意事項！</p>

        </div>
    );

}
export default About;
