import React,{ useState,useEffect } from 'react';
import {motion} from 'framer-motion';

const BodyText=(props)=>{
    const [rotation,setRotation]=useState(0);
    const [fontSize,setFontSize]=useState(1);
    const fontfamity=['serif','sans-serif','cursive','fantasy','monospace'];
    const rotateChange=(event)=>{
        const value=parseFloat(event.target.value);
        if(!isNaN(value)){
        setRotation(value)
        }else {
            setRotation(0)
        }     
    }

    const sizeChange=(event)=>{
            setFontSize(event.target.value)     
    }

    const fontChange=()=>{

    }
     //style={{fontFamily:'Menlo'}}

    console.log(rotation);
    return(
        <>
        <div style={{fontSize:'20px',fontFamily:'serif'}}>{props.text}の回転
            <input  onChange={(event)=>rotateChange(event)}
            type='number' min='0' max='360'
            value={rotation} 
            />

            <input  onChange={(event)=>rotateChange(event)}
            type='range' min='0' max='360'step='1'
            value={rotation}
            />
        </div>

        <div style={{fontSize:'20px'}}>{props.text}の大きさ
            <input onChange={(event)=>sizeChange(event)}
            type='number' min='1' max='20'
            value={fontSize}
            />

            <input onChange={(event)=>sizeChange(event)}
            type='range' min='1' max='20' step='1'
            value={fontSize}
            />
        </div>


            {fontfamity.map(()=>{
                return(
                    <button onClick={()=>fontChange}>
                    {fontfamity}
                    </button>
                )
            })}


        <motion.div 
         animate={{rotate:rotation,
                   scale:fontSize  }}

            drag
            whileHover={{
                scale:fontSize*1.5,
                transition:{duration:0.5},

            }}
        >{props.text}</motion.div>


    </>
    );
}
export default BodyText;
