import React,{ useState,useEffect } from 'react';
import {motion} from 'framer-motion';
import { SketchPicker } from 'react-color';


const BodyText=(props)=>{
    const [rotation,setRotation]=useState(0);
    const [fontSize,setFontSize]=useState(1);
    const fontfamily=['serif','sans-serif','cursive'];
    const [selectedfont,setSelectedfont]=useState('sans-serif');
    const [fontColor,setFontColor]=useState('rgba(0,0,0,1)');
    const [colorPalletOpen,setColorPalletOpen]=useState(false);
    const [paraState,setParaState]=useState(true);
    const [onceClose,setOnceClose]=useState(true);

    const rotateChange=(event)=>{
        const value=parseFloat(event.target.value);
        if(!isNaN(value)){
        setRotation(value)
        }else {
            setRotation(0)
        }     
    }

    const sizeChange=(event)=>{
            setFontSize(event.target.value)     
    }

    const fontChange=(value)=>{
            setSelectedfont(value)    
    }

    const colorChange=(color)=>{
        const rgbaColor = `rgba(${color.rgb.r}, ${color.rgb.g}, ${color.rgb.b}, ${color.rgb.a})`;
        setFontColor(rgbaColor);
    }

        useEffect(()=>{
        if(props.textlist>5 && onceClose){
         setParaState(false)
         setOnceClose(false)
         console.log(onceClose)
            }
        },[props.textlist])

    return(
    <>
        <button className='parastate' onClick={()=>{setParaState(prevstate=>!prevstate)}}>{props.text}：スタイル変更</button>
            {paraState ? (
                <div className='parameter'>

                        <button style={{width:'50%'}}onClick={()=>{setColorPalletOpen(prevstate=> !prevstate) }}>色を選択</button>

                        {colorPalletOpen ? (
                            <div className='pallet'>
                                <SketchPicker  onChange={colorChange} color={fontColor} />
                                <button onClick={()=>{setColorPalletOpen(prevstate=> !prevstate)}}>閉じる</button>
                            </div>
                            ) : (
                        <></>
                        )}

                        <div style={{fontSize:'20px'}}>{props.text}の回転
                            <input  onChange={(event)=>rotateChange(event)}
                            type='number' min='0' max='360'
                            value={rotation} 
                            />

                            <input  onChange={(event)=>{rotateChange(event)}}
                            type='range' min='0' max='360'step='1'
                            value={rotation}
                            />
                        </div>

                        <div style={{fontSize:'20px'}}>{props.text}の大きさ
                            <input onChange={(event)=>sizeChange(event)}
                            type='number' min='1' max='20'
                            value={fontSize}
                            />

                            <input onChange={(event)=>sizeChange(event)}
                            type='range' min='1' max='20' step='1'
                            value={fontSize}
                            />
                        </div>

                        <span style={{fontSize:'20px'}}>{props.text}のフォント
                            {fontfamily.map((value)=>{
                                return(
                                    <button onClick={()=>fontChange(value)}
                                    style={{backgroundColor:''}}
                                    >
                                <p>{value}</p>  
                                </button>
                                )
                            })}
                        </span>
                    </div>
            ):(
                <></>
            )}

        <motion.div className='text-move'
            animate={{
                    rotate:rotation,
                    scale:fontSize,
                    fontFamily:selectedfont,
                    color:fontColor 
                }}
            drag
            dragConstraints={props.bodyRef}
            whileHover={{
                scale:fontSize*1.5,
                transition:{duration:0.5},

            }}>
            {props.text}
        </motion.div>


    </>
    );
}
export default BodyText;
