            <div className='textparent'>
                <button onClick={countUp}>Up</button>
                <button onClick={countDown}>down</button>
                {textcount}
                {textlist.map((text,index)=>{
                    return(
                        <input 
                            onChange={(event)=>textValue(event,index)}
                            type='text'
                            value={text}
                        />
                    )
                })}
             </div>
                {textlist.map((text, index) => {
                    return (
                            <BodyText 
                            text={text}
                            />
                            )
                })}

        </body>
    );
}
export default Body;
