import React,{ useState,useEffect } from 'react';

const Body=()=>{


    const [createtext,setCreatetext]=useState(0)
    const [textlist,setTextlist]=useState([])
    const [htmltext,setHtmlext]=useState('')

    const countUp=()=>{
        const updatedText = createtext + 1;
        setCreatetext(updatedText)
        setTextlist(prevList=>[...prevList,updatedText])
    }

    const countDown=()=>{
        if(createtext>=1){
        setCreatetext(prevState => prevState-1)
        setTextlist(prevList=>prevList.slice(0,-1))
        }
    }

    const textValue=(event)=>{
        setHtmlext(event.target.value)
    }

    let textcount=(<div>{createtext}</div>);
   /* useEffect(()=>{ 
        textlist.push(1)  
    },[createtext])*/
    console.log(textlist);
    return(
        <body className="App-body">
            <button onClick={countUp}>Up</button>
            <button onClick={countDown}>down</button>
            {textcount}
            {textlist.map(()=>{
                return(
                    <input 
                        onChange={(event)=>textValue(event)}
                        type={'text'}
                        value={htmltext}
                    />
                )
            })}
        </body>
    );
}
export default Body;
